import { configureStore } from "@reduxjs/toolkit";
import cart from "./cartSlice";
import uiSlice from "./uiSlice";

const store = configureStore({
  reducer: { cartReducer: cart.reducer, uiReducer: uiSlice.reducer },
});

export default store;
