import { createSlice,createAsyncThunk } from "@reduxjs/toolkit";



const fetchUsersFromAPI = createAsyncThunk("users/fetchUsers", async function fetchUsers() {
    try {
        const response = await fetch(
            "https://jsonplaceholder.typicode.com/users"
        );

        const data = await response.json();

        console.log("data", data);
        console.log("before userArr", userArr);
        dispatch(userActions.addUser({ id: 1, name: "ZRonak", age: 28 }));
        console.log("after userArr", userArr);
    } catch (err) {
        console.log("ERRR is", err);
    }
})



const initialState = { users: [] };
export const userSlice = createSlice({
  name: "users",
  initialState,
  reducers: {
    addUser: (state, action) => {
      console.log("action", action);
      const newUser = action.payload;
      console.log("newUser", newUser);
      state.users.push(newUser);
    },
  },
});

export const userActions = userSlice.actions;
