import { createStore, applyMiddleware } from "redux";

const reuducerFunc = (state = { counter: 0 }, action) => {
  if (action.type === "INC") {
    return state.counter + 1;
  } else if (action.type === "DEC") {
    return state.counter - 1;
  } else {
    return state;
  }
};
const store = redux.createStore(reuducerFunc);

export default store;
